INSERT INTO facultades (name, location, description, image) VALUES ('Facultad de Filosofía y Letras - Colegio Caracciolos', ST_GeographyFromText('SRID=4326;POINT(-3.365169 40.48021)'), 'Calle de la Trinidad 3-5<br>28801 Alcalá de Henares<br>Madrid<br><br><br>En 1622 se realiza la construcción del colegio, consagrándolo a San José. Se sitúa en una manzana aislada rodeada por las calles Trinidad, Arcipreste, Matadero y San Julián. Se trata de un edificio de que se organiza alrededor de dos claustros separados por una amplia escalera imperial cubierta por una cúpula barroca elíptica. Tiene dos alturas en sus crujías principales al norte y al sur y tres en la crujía este y en el oeste completa el conjunto una esbelta iglesia. A principios del siglo XIX, con la invasión francesa, sufre como otros colegios menores de la ciudad, una serie de avatares que acaban poniendo fin a la primera parte de su vida con centro de enseñanza, uso para el que estaba expresamente construido.<br><br><br>Titulaciones que se imparten:<br><br>- <a href="http://www.uah.es/estudios/grados/planes.asp?cd=106&plan=G440&origen=estudios&T=ARTES_Y_HUMANIDADES" target="_blank">Grado en Lenguas Modernas y Traducción</a><br>- <a href="http://www.uah.es/estudios/grados/planes.asp?cd=106&plan=G790&origen=estudios&T=ARTES_Y_HUMANIDADES" target="_blank">Grado en Estudios Hispánicos</a><br>- <a href="http://www.uah.es/estudios/grados/planes.asp?cd=106&plan=G251&origen=estudios&T=ARTES_Y_HUMANIDADES" target="_blank">Grado en Estudios Ingleses</a>', 'https://lh5.googleusercontent.com/proxy/2nbb60lwcXqidtY9RfSyLMxrsBc9KEe5AQyrHpglh0f9KgWyyvF-pcqIOTg9n7ewfcJu_1tzKnPzgeDyy5PEnmBbTPqMV13dgbjnG3Jp-1W2UrOQMYqX9v5jtZZve6q5ZqQtU-9L7o5SFirv5g' );
INSERT INTO facultades (name, location, description, image) VALUES ('Facultad de Filosofía y Letras - Colegio de los Trinitarios', ST_GeographyFromText('SRID=4326;POINT(-3.363919 40.480675)'), 'Edificio Trinitarios<br><br>Calle trinidad 1-3<br>28801 Alcalá de Henares<br>Madrid<br><br>El edificio fue proyectado por un tracista desconocido hacia 1625 (su fundación es anterior: 1601) y construido con buen aparejo de ladrillo con cajones de mampuesto entre 1626 y 1650. <br>Se organiza en torno a un claustro cuadrado de dos plantas y a una gran iglesia con cúpula sobre el crucero cuyo volumen condiciona la traza general y cuya fachada se remata en el frontón. La Iglesia se eleva sobre el nivel de la calle gracias a una escalinata que permite la formación de una cripta ocupada por enterramientos; tiene planta de cruz latina de cuatro tramos, nártex, coro muy profundo, capilla mayor de testero plano y crucero con brazos de poca longitud que fusionan la capilla mayor con el cuerpo del templo. Responde al esquema que, codificado por Juan de Herrera y su discípulo Francisco de Mora, conformó el tipo de Iglesia propia del Siglo XVII.', 'https://lh5.googleusercontent.com/proxy/oZbsAaGlkQjgYK-uCU6KzpIp7-Y3XCoT4cW2zHxyYguJJUts9gCxo5r_99Lny9dQPAA9QZRq5fal40ZhSEp6eoIUBEfWOz7VcSc');
